import { Component, OnInit } from '@angular/core';
import {Chart} from 'chart.js/auto';
import { FoodDataService } from 'src/app/service/food-data.service';

@Component({
  selector: 'app-weight-graph',
  templateUrl: './weight-graph.component.html',
  styleUrls: ['./weight-graph.component.css']
})
export class WeightGraphComponent implements OnInit{
  public chart: any
  currentDate = new Date()
  weekAgo = new Date(this.currentDate)
  dateRange: Date[] = []

  constructor(private foodDataService: FoodDataService) {

  }

  ngOnInit(): void {
    this.weekAgo.setDate(this.currentDate.getDate() - 7)

    this.dateRange = [this.weekAgo, this.currentDate]

    this.createChart()


  }

  onDateChange(dateRange: Date[]) {
    this.createChart()
  }

  createChart() {
    var myChart = new Chart("myChart", {
      type: 'bar',
      data: {
          labels: this.foodDataService.getDateRange(this.dateRange),
          datasets: [{
              label: 'Weekly Macros',
              data: [{x: 10, y:8},{x: 10, y:8} ],
              backgroundColor: [
                  'rgba(255, 99, 132, 0.2)',

              ],
              borderColor: [
                  'rgba(255, 99, 132, 1)',

              ],
              borderWidth: 1
          }]
      },
      options: {
        maintainAspectRatio: false
      }
  });
  }

  onIncrementWeek() {
  }

  onDecrementWeek() {

  }


}
<div class="chart-container" fxLayout="column" fxLayoutGap="10px">
    <div fxLayoutAlign="center center" fxFlex="10"> 
        <button pButton class="date-navigation-buttons" (click)="onDecrementWeek()">
            <i class="pi pi-angle-left"></i>
        </button>
        <p-calendar [(ngModel)]="dateRange" (ngModelChange)="onDateChange($event)" selectionMode="range" [readonlyInput]="true" #calendar></p-calendar>
        <button pButton class="date-navigation-buttons" (click)="onIncrementWeek()">
            <i class="pi pi-angle-right"></i>
        </button>
    </div>
    <canvas id="myChart" class="chart" fxFlex="90"></canvas>


</div>
