import { Component, OnInit } from '@angular/core';
import {Chart} from 'chart.js/auto';

@Component({
  selector: 'app-weight-graph',
  templateUrl: './weight-graph.component.html',
  styleUrls: ['./weight-graph.component.css']
})
export class WeightGraphComponent implements OnInit{
  public chart: any
  selectedDate: Date


  ngOnInit(): void {
    var myChart = new Chart("myChart", {
      type: 'bar',
      data: {
          labels: ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'],
          datasets: [{
              label: 'Weekly Macros',
              data: [{x: 10, y:8},{x: 10, y:8} ],
              backgroundColor: [
                  'rgba(255, 99, 132, 0.2)',

              ],
              borderColor: [
                  'rgba(255, 99, 132, 1)',

              ],
              borderWidth: 1
          }]
      },
      options: {
        maintainAspectRatio: false
      }
  });
  }

  onIncrementWeek() {

  }

  onDecrementWeek() {

  }


}
<div class="chart-container" fxLayout="column" fxLayoutGap="10px">
    <div fxLayoutAlign="center center"> 
        <button pButton class="date-navigation-buttons" (click)="onDecrementWeek()">
            <i class="pi pi-angle-left"></i>
        </button>
        <p-calendar [(ngModel)]="selectedDate" [touchUI]="true" #calendar></p-calendar>
        <button pButton class="date-navigation-buttons" (click)="onIncrementWeek()">
            <i class="pi pi-angle-right"></i>
        </button>
    </div>

    <canvas id="myChart" class="chart"></canvas>

</div>
