    <div fxLayoutAlign="space-between" fxFlex="10"> 
        <label>Calorie Intake</label>
        <div fxLayout="row" fxLayoutAlign="center center" fxLayoutGap="10px">
        <label>Select Date:</label>
        <p-calendar class="date-select" [(ngModel)]="dateRange" [inline]="false" (ngModelChange)="onDateChange($event)" selectionMode="range" [readonlyInput]="true" #calendar></p-calendar>

        </div>
    </div>
    <canvas id="myChart" class="chart" fxFlex="90"></canvas>


</div>
