import { Injectable } from "@angular/core";
import { FoodLog } from "../models/FoodLog";
import { LocalStorageService } from "./local-storage.service";
import { TrackingService } from "./tracking.service";

@Injectable({
  providedIn: 'root',
})
export class FoodDataService {

    constructor(private trackingService: TrackingService,
        private localStorageService: LocalStorageService) {

    }

    getDailyCalories(date: Date) {
        console.log(this.localStorageService.getData('foodLogs'))

        const dailyCalories =this.localStorageService.getData('foodLogs').filter(log => {
            const logDate = new Date(log.time)

            logDate.getDate() == date.getDate() && logDate.getMonth() == date.getMonth()

        })
        return dailyCalories
    }

}
