import { Injectable } from "@angular/core";
import { FoodLog } from "../models/FoodLog";

@Injectable({
  providedIn: 'root',
})
export class LocalStorageService {
    foodStorageName: "foodLog"
    weightStorageName: "weightLog"


    setFoodData(storageName: string, data:any[]) {
        localStorage.setItem(this.foodStorageName, JSON.stringify(data))
    }

    getFoodData(): any[] {
        return JSON.parse(localStorage.getItem(this.foodStorageName))
    }

    setWeightData(storageName: string, data:any[]) {
        localStorage.setItem(this.weightStorageName, JSON.stringify(data))
    }

    getWeightData(): any[] {
        return JSON.parse(localStorage.getItem(this.weightStorageName))
    }
}
