import { Component, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';
import { FoodResult } from 'src/app/models/FoodResult';
import { FoodDbSearchService } from 'src/app/service/food-db-search.service';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent implements OnInit{
  searchQuery = new FormControl('')
  visible = false

  constructor(public foodSearchService: FoodDbSearchService) {

  }

  ngOnInit(): void {

  }

  onSubmit() {
    this.foodSearchService.getSearchResults(this.searchQuery.value)
  }

  toggleEditFood(food: FoodResult) {
    console.log(food)
    this.visible = !this.visible
  }

}
<div class="search-container" fxLayout="column" fxFill>
    <form (keydown.enter)="onSubmit()" fxLayout="column" fxFlex>
        <div class="container" fxLayout="column" fxLayoutAlign="start center" fxLayoutGap="10px">
            <p-dialog header="Edit Food" [(visible)]="visible" [style]="{width: '50vw'}">
                <app-edit></app-edit>
            </p-dialog>

            <span class="p-input-icon-left" fxLayout="column">
                <i class="pi pi-search"></i>
                <input 
                type="text" 
                pInputText 
                placeholder="Search for a food"
                [formControl]="searchQuery" />
            </span>
            <p-table class="search-table" [value]="foodSearchService.searchResults" [scrollable]="true"  scrollHeight="400px">
                <ng-template pTemplate="header" let-columns>
                    <tr>
                        <th >Name</th>
                        <th >Calories (kCal/100g)</th>
                        <th >Fat (g)</th>
                        <th >Carbs (g)</th>
                        <th >Protein (g)</th>
                    </tr>
                </ng-template>
                <ng-template pTemplate="body" let-rowData >
                    <tr (click)="toggleEditFood(rowData)">
                        <td >
                            {{ rowData.name}}
                        </td>
                        <td >
                            {{ rowData.calories}}
                        </td>
                        <td >
                            {{ rowData.fat}}
                        </td>
                        <td >
                            {{ rowData.carbs}}
                        </td>
                        <td >
                            {{ rowData.protein}}
                        </td> 
                    </tr>
                </ng-template>
            </p-table>
        </div>
    </form>

</div>
