import { Component, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';
import { FoodDbSearchService } from 'src/app/service/food-db-search.service';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.css']
})
export class SearchComponent implements OnInit{
  searchQuery = new FormControl('')

  constructor(private foodSearchService: FoodDbSearchService) {

  }

  ngOnInit(): void {

  }

  onSubmit() {
    this.foodSearchService.getSearchResults(this.searchQuery.value)
  }

}
<div class="search-container" >
    <div class="container" fxLayout="column" fxLayoutAlign="start center" fxLayoutGap="10px">
        <form (keydown.enter)="onSubmit()">
            <span class="p-input-icon-left" fxLayout="column">
                <i class="pi pi-search"></i>
                <input 
                type="text" 
                pInputText 
                placeholder="Search for a food"
                [formControl]="searchQuery" />
            </span>
        </form>

    </div>


</div>
